import {
    SET_LANGUAGE_E,
    SET_LANGUAGE_S
} from '../types'

const initialState = true

export default function language (state = initialState, action) {
    const { type, payload } = action;

    switch (type) {
        case SET_LANGUAGE_E:
            return {
            ...state = true
            };
        case SET_LANGUAGE_S:
            return {
            ...state = false
            };
        default:
            return state;
    }
  }
