import React, {useEffect} from 'react'

//Components
import Layout from '../components/layout'

import '../style.scss'

const App = ({ Component, pageProps }) => {


    //Content Fader
    useEffect(() => {
        const fadeIn = document.querySelectorAll(".fade-in")
        const slideUp = document.querySelectorAll(".fade-in-up")
        const slideLeft = document.querySelectorAll(".slide-in-left")
        const slideRight = document.querySelectorAll(".slide-in-right")
        const slideRightDelay = document.querySelectorAll(".slide-in-right")

        const fadeInOps = {
            root: null,
            threshold: 0,
            rootMargin: "-100px"
        }

        const scrollFade = new IntersectionObserver(function(entries, scrollFade){
            entries.forEach(entry => {
            if(!entry.isIntersecting) {
                return;
            } else {
                entry.target.classList.add('appear')
                scrollFade.unobserve(entry.target)
            }
            })
        }, fadeInOps)

        fadeIn.forEach(fader => {
            scrollFade.observe(fader)
        });

        slideLeft.forEach(fader => {
            scrollFade.observe(fader)
        });

        slideRight.forEach(fader => {
            scrollFade.observe(fader)
        });



    }, [])






    return (
        <Layout>
            <Component {...pageProps} />
        </Layout>
    )
}

export default App;
