import React, {useEffect} from 'react'

//Components
import Layout from '../components/layout'

import "react-responsive-carousel/lib/styles/carousel.min.css";
import '../style.scss'

const App = ({ Component, pageProps }) => {
    useEffect(() => {
        const fadeIn = document.querySelectorAll(".fade-in")
        const fadeInDelay = document.querySelectorAll(".fade-in-delay")
        const slideLeft = document.querySelectorAll(".slide-in-left")
        const slideRight = document.querySelectorAll(".slide-in-right")
        const slideRightDelay = document.querySelectorAll(".slide-in-right-delay")

        const fadeInOps = {
            root: null,
            threshold: 0,
            rootMargin: "-100px"
        }

        const scrollFadeIn = new IntersectionObserver(function(entries, scrollFade){
            entries.forEach(entry => {
            if(!entry.isIntersecting) {
                return;
            } else {
                entry.target.classList.add('appear')
                scrollFade.unobserve(entry.target)
            }
            })
        }, fadeInOps)

        fadeIn.forEach(fader => {
            scrollFadeIn.observe(fader)
        });

        fadeInDelay.forEach(fader => {
            scrollFadeIn.observe(fader)
        });

        slideLeft.forEach(fader => {
            scrollFadeIn.observe(fader)
        });

        slideRight.forEach(fader => {
            scrollFadeIn.observe(fader)
        });

        slideRightDelay.forEach(fader => {
            scrollFadeIn.observe(fader)
        });


        const fadeOut = document.querySelectorAll(".fade-out")
        const slideUpOut = document.querySelectorAll(".slide-up-out")

        const fadeOutOps = {
            root: null,
            threshold: 0,
            rootMargin: "-45%",
        }

        const scrollFadeOut = new IntersectionObserver(function(entries, scrollFade){
            entries.forEach(entry => {
            if(!entry.isIntersecting) {
                console.log('not intersect')
                return (
                    entry.target.classList.remove('out')
                )
            } else {
                entry.target.classList.add('out')
            }
            })
        }, fadeOutOps)

        fadeOut.forEach(fader => {
            scrollFadeOut.observe(fader)
        });

        slideUpOut.forEach(fader => {
            scrollFadeOut.observe(fader)
        });

    }, [])


    return (
        <Layout>
            <Component {...pageProps} />
        </Layout>
    )
}

export default App;
