import React, {useEffect} from 'react'

//Components
import Layout from '../components/layout'

import '../style.scss'

const App = ({ Component, pageProps }) => {

    useEffect(() => {

    }, [])

    //Content Fader
    const fadeIn = document.querySelectorAll(".fade-in")
    const slideUp = document.querySelectorAll(".fade-in-up")
    const slideLeft = document.querySelectorAll(".fade-in-left")
    const slideRight = document.querySelectorAll(".fade-in-right")

    const fadeOptions = {
        root: null,
        threshold: 0,
        rootMargin: "-100px"
    }

    const scrollFade = new IntersectionObserver(function(entries, scrollFade){
        entries.forEach(entry => {
        if(!entry.isIntersecting) {
            return;
        } else {
            entry.target.classList.add('appear')
            scrollFade.unobserve(entry.target)
        }
        })
    }, fadeOptions)

    faderIn.forEach(fader => {
        scrollFade.observe(fader)
    });

    faderUp.forEach(fader => {
        scrollFade.observe(fader)
    });





    return (
        <Layout>
            <Component {...pageProps} />
        </Layout>
    )
}

export default App;
